mapper.select:
select 

from re_raceteammember
where rid = #{rid,jdbcType=VARCHAR}
  and tid = #{tid,jdbcType=VARCHAR}
  and tmid = #{tmid,jdbcType=VARCHAR}

mapper.delete:
delete from re_raceteammember
where rid = #{rid,jdbcType=VARCHAR}
  and tid = #{tid,jdbcType=VARCHAR}
  and tmid = #{tmid,jdbcType=VARCHAR}
